def run(data):
    data = data.strip()
    lines = [d.split() for d in data.split("\n")]
    in_lines = [tuple(float(d) for d in line) for line in lines]
    for i in zip(*in_lines):
        print("{:.2f}".format(sum(i) / len(i)))


